mapper.insert:

INSERT
INTO "USER"
(
	US_NO
  , US_ID
  , US_PWD
  , US_NAME
  , US_BIRTH
  , US_EMAIL
  , US_PHONE
  , BUS_NO
  , US_PART
  , US_STATUS
)
VALUES
(
    USER_SEQ_NO.NEXTVAL
  , #{usid}
  , #{uspwd}
  , #{usname}
  , #{usbirth}
  , #{usemail}
  , #{usphone}
  , #{busno} 
  , #{uspart}
  , DEFAULT
)

mapper.update:
UPDATE
		"USER"
   SET
   		US_PWD = #{uspwd}
 WHERE
		US_ID = #{usid}
   AND  US_NAME = #{usname}
   AND  US_STOP IS NULL


        UPDATE
              "USER"


				US_NAME = #{usname}, 


				US_EMAIL = #{usemail}, 


				BUS_NO = #{busno}, 


				US_PHONE = #{usphone}


		 WHERE
			    US_PART = #{uspart}
		   AND  US_ID = #{usid}

mapper.select:

SELECT
       *
  FROM
       "USER"	



			US_NO LIKE #{sv}


			AND US_ID LIKE #{sv}


			AND US_NAME LIKE #{sv}


			AND US_BIRTH LIKE #{sv}


			AND US_EMAIL LIKE #{sv}


			AND US_PHONE LIKE #{sv}


			AND BUS_NO LIKE #{sv}


			AND US_STATUS LIKE #{sv}


			AND (US_NO LIKE #{sv}
				 OR US_ID LIKE #{sv}
				 OR US_NAME LIKE #{sv}
				 OR US_BIRTH LIKE #{sv}
				 OR US_EMAIL LIKE #{sv}
				 OR US_PHONE LIKE #{sv}
				 OR BUS_NO LIKE #{sv}
				 OR US_STATUS LIKE #{sv})



AND US_PART <> '관리자' 
	  ORDER BY US_NO
